/**
 * Format the error according to JsonWebToken (JWT)
 *
 * @function
 * @param {Object} err - Error from JsonWebToken (JWT)
 * @returns {Object} - Formatted error object to be passed to formatError
 */
function jwtError(err) {
  // Defaults are set to fit the majority of use cases
  const error = {
    level: 'warn',
    info: undefined,
    message: err.message,
    statusCode: 400,
    jsonResponse: {
      status: 'fail',
      name: err.name,
      data: { jwt: err.message },
    },
  };

  switch (err.message) {
    case 'jwt expired':
    case 'jwt malformed':
    case 'jwt signature is required':
    case 'jwt must be provided':
    case 'invalid signature':
      return error;
    default:
      error.statusCode = 500;
      error.level = 'error';
      error.jsonResponse.status = 'error';
      error.jsonResponse.message = err.message;

      return error;
  }
}

module.exports = jwtError;
