/**
 * Format the error according to manually thrown errors or unknown errors
 *
 * @function
 * @param {Object} err - Error thrown from controller
 * @returns {Object} - Formatted error object to be passed to formatError
 */
function appError(err) {
  // Defaults are set to fit the majority of use cases
  const error = {
    level: 'warn',
    message: err.message,
    statusCode: err.statusCode,
    jsonResponse: err.errors,
  };

  switch (err.statusCode) {
    case '400':
    case '403':
      return error;
    default:
      error.level = 'error';

      return error;
  }
}

module.exports = appError;
