const objectPath = require('object-path');

/**
 * Format the error according to manually thrown errors or unknown errors
 *
 * @function
 * @param {Object} err - Error thrown from controller
 * @returns {Object} - Formatted error object to be passed to formatError
 */
function defaultError(err) {
  // Set a default properties
  const statusCode = objectPath.get(err, 'statusCode', '500');
  const code = objectPath.get(err, 'code', 'SERVER_ERROR');
  const meta = objectPath.get(err, 'meta', undefined);

  if (meta) {
    objectPath.set(meta, 'path', err.meta.path);
  }

  // Defaults are set to fit the majority of use cases
  return {
    level: 'error',
    message: err.message,
    statusCode,
    jsonResponse: [{
      statusCode,
      message: err.message,
      code,
      meta,
    }],
  };
}

module.exports = defaultError;
