const methods = require('./user-methods');

/**
 * User model formatters
 *
 * @key {String} - The name of a model database table
 * @value {Object} - The formatters for fields in the model database table
 */
module.exports = {
  users: {
    first_name: value => value.toLowerCase(),
    last_name: value => value.toLowerCase(),
    email: value => value.toLowerCase(),
    password: value =>
      methods.hashPassword(null, value)
        .then(result => result.hashedPassword),
  },
};
