/**
 * Required validator
 *
 * @description Checks for null, undefined, or empty string; Makes fields required
 * @function
 * @param {(String|Number|Date)} value - Value passed from the model
 * @returns {Error} - Throws a new error
 */
const required = (value) => {
  if (value === null || value === undefined || value === '') {
    throw new Error('Required');
  }
};

/**
 * Suspicious validator
 *
 * @description Checks &<>'"/ unwanted characters; Sanitization
 * @function
 * @param {(String|Number|Date)} value - Value passed from the model
 * @returns {Error} - Throws a new error
 */
const suspicious = (value) => {
  if (/[&<>'"/]/.test(value)) {
    throw new Error('Suspicious characters [&<>"\',/]');
  }
};

module.exports = {
  required,
  suspicious,
};
