const validate = require('validate.js');

const validator = (attributes, constraints) => {
  const validations = validate(attributes, constraints);

  if (validations) {
    const serviceError = {
      name: 'ValidationError',
      statusCode: 400,
      data: validations,
    };

    throw (serviceError);
  }

  return validations;
};

module.exports = validator;
